package a75f.io.renatus.profiles.systemimport a75f.io.api.haystack.CCUHsApiimport a75f.io.device.mesh.DeviceUtilimport a75f.io.device.mesh.MeshUtilimport a75f.io.domain.api.Domainimport a75f.io.domain.api.DomainNameimport a75f.io.domain.logic.hasChangesimport a75f.io.domain.util.ModelLoaderimport a75f.io.logger.CcuLogimport a75f.io.logic.Globalsimport a75f.io.logic.Limport a75f.io.logic.bo.building.dab.DabProfile.CARRIER_PRODimport a75f.io.logic.bo.building.definitions.ProfileTypeimport a75f.io.logic.bo.building.system.dab.DabFullyModulatingRtuimport a75f.io.logic.bo.building.system.vav.config.DabModulatingRtuProfileConfigimport a75f.io.logic.bo.haystack.device.ControlMoteimport a75f.io.logic.bo.util.DesiredTempDisplayModeimport a75f.io.renatus.BuildConfigimport a75f.io.renatus.profiles.oao.updateOaoPointsimport a75f.io.renatus.profiles.profileUtils.UnusedPortsModelimport a75f.io.renatus.util.ProgressDialogUtilsimport a75f.io.renatus.util.highPriorityDispatcherimport android.content.Contextimport androidx.lifecycle.viewModelScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport kotlinx.coroutines.withContextopen class DabModulatingRtuViewModel : DModulatingRtuViewModel() {    fun init(context: Context, hayStack: CCUHsApi) {        super.init(context, ModelLoader.getDabModulatingRtuModelDef(), hayStack)        val systemEquip = hayStack.readEntity("system and equip and not modbus and not connectModule")        CcuLog.i(Domain.LOG_TAG, "Current System Equip $systemEquip")        if (systemEquip["profile"].toString() == "dabFullyModulatingAhu"            || systemEquip["profile"].toString() == ProfileType.SYSTEM_DAB_ANALOG_RTU.name        ) {            CcuLog.i(Domain.LOG_TAG, "Get active config for dabFullyModulatingAhu")            profileConfiguration = DabModulatingRtuProfileConfig(model).getActiveConfiguration()            viewState.value = DabModulatingRtuViewState.fromProfileConfig(profileConfiguration)            CcuLog.i(Domain.LOG_TAG, "Active dabFullyModulatingAhu profile config loaded")        } else {            profileConfiguration = DabModulatingRtuProfileConfig(model).getDefaultConfiguration()            viewState.value = DabModulatingRtuViewState.fromProfileConfig(profileConfiguration)            CcuLog.i(Domain.LOG_TAG, "Default dabFullyModulatingAhu profile config Loaded")        }        initialPortValues = HashMap(profileConfiguration.unusedPorts)        modelLoadedState.postValue(true)        viewState.value.isSaveRequired = !systemEquip["profile"].toString().contentEquals("dabFullyModulatingAhu")    }    override fun saveConfiguration() {        hayStack.resetCcuReady()        var systemEquipId : String? = null        if(BuildConfig.BUILD_TYPE == CARRIER_PROD){            ProgressDialogUtils.showProgressDialog(context,"Saving VVT-C Configuration")        } else {            ProgressDialogUtils.showProgressDialog(context, "Saving DAB Configuration")        }        viewModelScope.launch (highPriorityDispatcher) {            val systemEquip = hayStack.readEntity("system and equip and not modbus and not connectModule")            if (systemEquip["profile"].toString() != "dabFullyModulatingAhu"                && systemEquip["profile"].toString() != ProfileType.SYSTEM_DAB_ANALOG_RTU.name            ) {                viewState.value.updateConfigFromViewState(profileConfiguration)                deleteSystemProfile(systemEquip["id"].toString())                systemEquipId = createNewEquip(systemEquip["id"].toString())                L.ccu().systemProfile = DabFullyModulatingRtu()                L.ccu().systemProfile.removeSystemEquipModbus()                L.ccu().systemProfile.addSystemEquip()                CcuLog.i(Domain.LOG_TAG, profileConfiguration.toString())                UnusedPortsModel.saveUnUsedPortStatusOfSystemProfile(                    profileConfiguration,                    hayStack                )                profileConfiguration.unusedPorts.clear()                profileConfiguration.unusedPorts = ControlMote.getCMUnusedPorts(Domain.hayStack)                viewState.value.unusedPortState = profileConfiguration.unusedPorts            }else{                super.saveConfiguration()            }            withContext(Dispatchers.Main) {                ProgressDialogUtils.hideProgressDialog()            }            DesiredTempDisplayMode.setSystemModeForDab(hayStack)            updateDabSystemMode()            systemEquipId?.let { L.ccu().systemProfile.updateAhuRef(it) }            updateOaoPoints()            hayStack.syncEntityTree()            hayStack.setCcuReady()            initialPortValues = HashMap(profileConfiguration.unusedPorts)        }    }    override fun sendAnalogRelayTestSignal(tag: String, value: Double) {        Globals.getInstance().setTestMode(true)        if (tag.contains("analog")) {            when(tag){                DomainName.analog1Out ->                    Domain.cmBoardDevice.analog1Out.writeHisVal(value)                DomainName.analog2Out ->                    Domain.cmBoardDevice.analog2Out.writeHisVal(value)                DomainName.analog3Out ->                    Domain.cmBoardDevice.analog3Out.writeHisVal(value)                DomainName.analog4Out ->{                    Domain.cmBoardDevice.analog4Out.writeHisVal(value)                }            }        } else if (tag.contains("relay")) {            ControlMote.setRelayState(tag, value)        }        MeshUtil.sendStructToCM(DeviceUtil.getCMControlsMessage())    }    fun reset() {        val systemEquip = hayStack.readEntity("system and equip and not modbus and not connectModule")        if (systemEquip["profile"].toString() == "dabFullyModulatingAhu"            || systemEquip["profile"].toString() == ProfileType.SYSTEM_DAB_ANALOG_RTU.name        ) {            profileConfiguration = DabModulatingRtuProfileConfig(model).getActiveConfiguration()            CcuLog.i(Domain.LOG_TAG, "Active dabFullyModulatingAhu profile config Loaded")        }else{            profileConfiguration = DabModulatingRtuProfileConfig(model).getDefaultConfiguration()            CcuLog.i(Domain.LOG_TAG, "Default dabFullyModulatingAhu profile config Loaded")        }        viewState.value = DabModulatingRtuViewState.fromProfileConfig(profileConfiguration)        viewState.value.isSaveRequired = !systemEquip["profile"].toString().contentEquals("dabFullyModulatingAhu")    }    fun hasUnsavedChanges(): Boolean {        return try {            val newConfiguration = DabModulatingRtuProfileConfig(model).getDefaultConfiguration()            viewState.value.updateConfigFromViewState(newConfiguration)            return hasChanges(                profileConfiguration,                newConfiguration            ) || (initialPortValues.toMap() != viewState.value.unusedPortState.toMap())        } catch (e: Exception) {            false        }    }}